import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import ReactDOM from 'react-dom';






class LoginControl extends React.Component
{
    constructor()
    {
      super();
      this.logIn = this.logIn.bind(this);
      this.logOut = this.logOut.bind(this);
      this.state = {isLogIn: false}
    }
    /*Log in method*/
    //logIn = () => this.setState({isLogIn:true});
    logIn() { this.setState({isLogIn:true})}
    /*Log out method*/
    logOut() { this.setState({isLogIn:false});}

    render(){
      let logInOut = this.state.islogIn;
      console.log(logInOut)
      let submit;
      if(logInOut){
        submit = <LogOutButtom onClick={this.logOut}/>;
      }
      else{
        submit = <LogInButtom onClick={this.logIn}/>;
      }
      return <div>
                <Confirmation isLogIn = {logInOut} />
                {submit}
             </div>
    }
}
let Confirmation = (props) => {
  if(props.isLogIn ){return <h1>Login confirmed!</h1> }
  else{ return <h1>Invalid Login</h1> }
}
let LogInButtom = (props) => <button onClick={props.onClick}> Log In </button>;
let LogOutButtom = (props) => <button onClick={props.onClick}> Log Out </button>;
ReactDOM.render(
  <LoginControl/>,
  document.getElementById('root')

);
