import React from 'react';
import ReactDOM from 'react-dom';
import LifeCycles from './components/LifeCycles';
const style = {
  width:"300px",
  height: "200px",
  backgroundColor:"lightblue"
}
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      number1: 0,
      number2: 0,
      sum: 0
    }
  }

  add         = () => this.setState({sum:this.state.number1 + this.state.number2})
  subtraction = () => this.setState({sum:this.state.number1 - this.state.number2})
  product     = () => this.setState({sum:this.state.number1 * this.state.number2})
  division    = () => this.setState({sum:this.state.number1 / this.state.number2})


  getNumber1 = (event) => this.setState({number1:Number(event.target.value)})
  getNumber2 = (event) => this.setState({number2:Number(event.target.value)})

  render(){
    return (
      <div style={style}>
          <div><input onKeyUp={this.getNumber1} placeholder="enter number"/></div>
          <div><input onKeyUp={this.getNumber2}  placeholder="enter number"/></div>
          <div><button onClick={this.add}>Add</button></div>
          <div><button onClick={this.subtraction}>Subtraction</button></div>
          <div><button onClick={this.product}>Product</button></div>
          <div><button onClick={this.division}>Dividion</button></div>
          {this.state.sum}
      </div>
    )
  }

}


ReactDOM.render(
  <App/>,
  document.getElementById('root')

);


