import React from 'react';
import ReactDOM from 'react-dom';
import LifeCycles from './components/LifeCycles';
let style = {
  display:"none"
}
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      userLogin: false,
      userName: '',
      userPassword: '',
      user: {
        userName: 'A',
        userPassword: 'password'
      }

    }
  }

  logIn = () => {
    if(this.state.userName==this.state.user.userName &&
       this.state.userPassword==this.state.user.userPassword){
         this.setState({userLogin:true})
       }

  }
  getUserName = (event) =>
  {
    let user = event.target.value;
    this.setState({userName:user})
  }
  getUserPassword = (event) =>
  {
    let password = event.target.value;
    this.setState({userPassword:password})
  }
  render(){
    let data;
    console.log(this.state.userLogin);
    if(this.state.userLogin)
     {
       data = <h1 className="welcome" >Welcome login</h1>;
     }
     else if(!this.state.userLogin){
       data = <h1 className="error" >Invalid data</h1>;
     }
    return (
      <div>
          <div><input onKeyUp={this.getUserName} placeholder="username"/></div>
          <div><input onKeyUp={this.getUserPassword}  placeholder="password"/></div>
          <div><button onClick={this.logIn}>LogIn</button></div>
          {data}
      </div>
    )
  }

}


ReactDOM.render(
  <App/>,
  document.getElementById('root')

);

