import React from 'react';
import ReactDOM from 'react-dom';
import { v4 as uuidv4 } from 'uuid';


class LifeCycles extends React.Component{
  constructor(props){
    super(props);

  }
  componentDidMount = () => {

  }
  isArray = (array) => Object.isArray(array);
  render(){
    let current;
    if(isArray(this.props.language)){
    current = this.props.language.map((item) =>  <li id={uuidv4()}>{item}</li> );
    }
    else{
      current = this.props.language;
    }
    return <ul>{current}</ul>
  }
}
export default LifeCycles;
