import React from 'react';
import ReactDOM from 'react-dom';
import LifeCycles from './components/LifeCycles';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      number1: 0,
      number2: 0,
      sum: 0
    }
  }

  add = () => {

    let sumUp = this.state.number1 + this.state.number2;
    this.setState({sum:sumUp})
  }
  subtraction = () => {
    let result = this.state.number1 - this.state.number2;
    this.setState({sum:result})
  }

  product = () => {
    let result = this.state.number1 * this.state.number2;
    this.setState({sum:result})
  }
  division = () =>{
    let result = this.state.number1 / this.state.number2;
    this.setState({sum:result})
  }


  getNumber1 = (event) =>
  {
    let num = Number(event.target.value);
    this.setState({number1:num})
  }
  getNumber2 = (event) =>
  {
    let num = Number(event.target.value);
    this.setState({number2:num})
  }
  render(){
    return (
      <div>
          <div><input onKeyUp={this.getNumber1} placeholder="enter number"/></div>
          <div><input onKeyUp={this.getNumber2}  placeholder="enter number"/></div>
          <div><button onClick={this.add}>Add</button></div>
          <div><button onClick={this.subtraction}>Subtraction</button></div>
          <div><button onClick={this.product}>Product</button></div>
          <div><button onClick={this.division}>Dividion</button></div>
          {this.state.sum}
      </div>
    )
  }

}


ReactDOM.render(
  <App/>,
  document.getElementById('root')

);


